import { EventEmitter } from '@angular/core';
import { DeleteDialogComponent, DeleteState } from 'src/app/dialogs/delete-dialog/delete-dialog.component';
import { MessageType, Position } from 'src/app/services/admin/alertify.service';
import * as i0 from "@angular/core";
import * as i1 from "src/app/services/common/http-client.service";
import * as i2 from "@angular/material/dialog";
import * as i3 from "src/app/services/admin/alertify.service";
export class DeleteDirective {
    constructor(element, _render, httpClientService, dialog, alertifyService) {
        this.element = element;
        this._render = _render;
        this.httpClientService = httpClientService;
        this.dialog = dialog;
        this.alertifyService = alertifyService;
        this.callback = new EventEmitter();
        const img = _render.createElement("img");
        img.setAttribute("src", "../../../../../assets/delete.png");
        img.setAttribute("style", "cursor: pointer;");
        img.width = 25;
        img.height = 25;
        _render.appendChild(element.nativeElement, img);
    }
    async onclick() {
        this.openDialog(async () => {
            const td = this.element.nativeElement;
            $(td.parentElement).fadeOut(1000);
            this.httpClientService.delete({
                controller: this.controller
            }, this.id).subscribe(data => {
                this.callback.emit();
                this.alertifyService.message("Ürün Başarıyla Silindi", {
                    dismissOthers: true,
                    messageType: MessageType.Success,
                    position: Position.TopRight
                });
            });
        });
    }
    openDialog(afterClosed) {
        const dialogRef = this.dialog.open(DeleteDialogComponent, {
            width: '250px',
            data: DeleteState.Yes,
        });
        dialogRef.afterClosed().subscribe(result => {
            if (result == DeleteState.Yes) {
                afterClosed();
            }
        });
    }
}
